            , farmRoomInternet
            , farmRoomSite
            , farmRoomInfo
            , farmRoom
            , farmRoomUnisex
            , farmRoomEtc
            , userName
            , farmMapx
            , farmMapy)
	    VALUES 
            ( #{farmImg}
            , #{farmName}
            , #{farmStartOpen}
            , #{farmProduce}
            , #{farmType}
            , #{farmerIntro}
            , #{farmAddr}
            , #{farmAddrDetail}
            , #{farmRoomInternet}
            , #{farmRoomSite}
            , #{farmRoomInfo}
            , #{farmRoom}
            , #{farmRoomUnisex}
            , #{farmRoomEtc}
            , #{userName}
            , #{farmMapx}
            , #{farmMapy} )

mapper.delete:
		DELETE FROM farm_room_img
		WHERE

                roomImgFarmCode = #{roomImg.farmCode}
                AND
                roomImgUrl = #{roomImg.roomImgUrl}

mapper.update:
        UPDATE farm
        SET
             farmState       = '신청중'
            ,farmAskDate     = NOW()
            ,farmRegDate     = ''
		    ,farmName        = #{farmName}
		    ,farmStartOpen   = #{farmStartOpen}
		    ,farmProduce     = #{farmProduce}
		    ,farmType        = #{farmType}
		    ,farmerIntro     = #{farmerIntro}
		    ,farmAddr        = #{farmAddr}
		    ,farmAddrDetail  = #{farmAddrDetail}
		    ,farmRoomInternet= #{farmRoomInternet}
		    ,farmRoomSite    = #{farmRoomSite}
		    ,farmRoomInfo    = #{farmRoomInfo}
		    ,farmRoom        = #{farmRoom}
		    ,farmRoomUnisex  = #{farmRoomUnisex}
		    ,farmRoomEtc     = #{farmRoomEtc}
		    ,userName        = #{userName}
            ,farmMapx        = #{farmMapx}
            ,farmMapy        = #{farmMapy}

            ,farmImg         = #{farmImg}

	    WHERE 
             farmCode        = #{farmCode}
